INSERT INTO `wp_posts` VALUES (7, 1, '2024-1-12 00:33:40', '2024-1-11 16:33:40', '/* banner */\n.astra-customize-banner .wp-block-uagb-advanced-heading {\n	text-align: right;\n}\n.astra-customize-banner .uagb-heading-text,\n.astra-customize-banner .uagb-desc-text\n{\n	color: #fff;\n}\n/* sulution */\n.wp-block-uagb-container .wp-block-uagb-container .solution-item figure {\n	overflow: hidden;\n}\n/* news */\n#whatsnew {\n	padding: 50px 0;\n}\n#whatsnew a {\n	text-decoration: none;\n}\n#whatsnew p {\n	margin-bottom: 0;\n}\n#whatsnew [class*=\"a-btn\"] {\n	display: inline-block;\n	position: relative;\n	z-index: 1;\n	margin: auto;\n	overflow: hidden;\n	border: 2px solid #666;\n	padding: 0.8em 40px 0.8em 30px;\n	line-height: 1.25;\n	min-width: 30%;\n	max-width: 12em;\n	background: transparent url(http://localhost:7777/wp-content/uploads/2024/01/arrowbtn-1.png) right center no-repeat;\n	color: #666;\n	letter-spacing: .04em;\n	text-align: center;\n}\n#whatsnew [class*=\"a-btn\"]:hover {\n	color: #fff;\n	background-color: var(--ast-global-color-0);\n}\n#whatsnew [class*=\"a-btn\"] span {\n	display: inline-block !important;\n}\n#whatsnew>h3 {\n	font-weight: 500;\n	font-size: 20px;\n	padding: 0 0 20px 10px;\n	margin: 0;\n	background: none;\n	border: 0;\n	box-shadow: none;\n	text-align: left;\n}\n#whatsnew>h3>span[lang=\"en\"] {\n	display: table-cell;\n	padding: 0;\n	font-family: arial;\n	font-size: 30px;\n	font-weight: 700;\n	line-height: 1;\n	color: var(--ast-global-color-0);\n}\n#whatsnew>h3>span[lang=\"en\"]::after {\n	content: \'\';\n	border-left: solid 1px #c8c9ca;\n	margin: 0 20px;\n}\n#whatsnew>h3>span+span,\n#whatsnew .news-tag \n{\n	display: table-cell;\n	background-color: var(--ast-global-color-0);\n	color: #fff;\n	font-size: 12px;\n	vertical-align: middle;\n	padding: 0 15px;\n	line-height: 15px;\n}\n#whatsnew .news-tag {\n	line-height: 30px;\n	margin-right: 10px;\n}\n#whatsnew .content {\n	margin: 0 auto;\n	overflow: hidden;\n	position: relative;\n	text-align: left;\n	width: 100%;\n	padding: 0 10px;\n	max-width: 1140px;\n}\n\n#whatsnew .content .newlist {\n	margin: 0 auto 20px auto;\n	padding: 0;\n}\n#whatsnew .content .newlist>li {\n	clear: both;\n	padding: 1.25em 10px;\n	overflow: hidden;\n	position: relative;\n	text-align: left;\n	display: flex;\n	flex-direction: column;\n}\n#whatsnew .content .newlist>li p {\n	display: flex;\n	flex-direction: column;\n}\n#whatsnew .content .newlist>li:nth-child(odd) {\n	background-color: #f8f8f8;\n}\n#whatsnew .t-c {\n	text-align: center;\n}\n#whatsnew .t-c .omit {\n	display: block;\n	overflow: hidden;\n	width: 0.5em;\n	height: 1em;\n	line-height: 5em;\n	float: left;\n	margin: 0 0 0 -0.5em;\n	position: absolute;\n	left: 0;\n	top: -1em;\n	background: transparent none;\n}\n@media (min-width: 48em) {\n	\n	#whatsnew>h3 {\n		width: 100%;\n		max-width: 1140px;\n		margin: 0 auto;\n		padding-bottom: 40px;\n	}\n	#whatsnew .newlist>li>time {\n		margin: 0 1em 0 0;\n	}\n	#whatsnew .content .newlist>li {\n		flex-direction: row;\n	}\n	#whatsnew .content .newlist>li p {\n		display: flex;\n		flex-direction: row;\n	}\n}\n/* pickup */\n#pickup {\n	margin: 0 auto;\n}\n#pickup li {\n	background: url(https://www.plush.co.jp/images/design/bg_business.png) center center no-repeat;\n	background-size: cover;\n	list-style: none;\n}\n#pickup li a {\n	text-align: center;\n	font-size: 26px;\n	font-size: 5.4vw;\n	padding: 2em 2em 1em;\n	\n	text-decoration: none;\n}\n#pickup a span[lang=\"en\"] {\n	font-family: arial;\n	font-size: 26px;\n	font-weight: 700;\n	display: block;\n	color: #fff;\n	opacity: 1;\n}\n#pickup a span {\n	display: block;\n	color: #fff;\n	opacity: 1;\n}\n\n#pickup a span+span {\n	font-size: 14px;\n}\n#pickup a span:last-child {\n	padding-top: 4em;\n	position: relative;\n	padding-left: 40px;\n}\n#pickup a span:last-child::before {\n	content: \'\';\n	display: inline-block;\n	position: absolute;\n	width: 30px;\n	border-bottom: 1px solid #fff;\n	left: calc(50% - 40px);\n	bottom: 9px;\n	text-decoration: inherit;\n	vertical-align: inherit;\n}\n#pickup li a:hover {\n	background-color: rgba(0,0,0,0);\n}\n@media (min-width: 40em) {\n	#pickup li {\n		display: table;\n		table-layout: fixed;\n		width: 33.33%;\n		float: left;\n	}\n	#pickup li a {\n		display: table-cell;\n		vertical-align: middle;\n		font-size: 3vw;\n	}\n}\n@media (min-width: 48em) {\n	#pickup li a {\n		font-size: 26px;\n		background-color: rgba(0,0,0,0.4);\n	}\n}\n@media (min-width: 61em) {\n	#pickup li a {\n    padding: 4.2em 2em 2em;\n	}\n}\n\n\n\n\n/* section-footer-content */\n.cotent-warp .uagb-ifb-desc a {\n	text-decoration: none;\n	color: #fff;\n}\n/* footer */\n.footer-item p {\n	color: #fff !important;\n}', 'astra', '', 'publish', 'closed', 'closed', '', 'astra', '', '', '2024-1-13 21:58:21', '2024-1-13 13:58:21', '', 0, 'http://localhost:7777/?p=7', 0, 'custom_css', '', 0);
